<script lang="ts">
export default {
  name: 'LogoStoryblok',

  props: {
    textVariant: {
      type: String,
      default: 'dark',
      validations: (val) => {
        return ['dark', 'white'].includes(val)
      },
    },
    iconOnly: {
      type: Boolean,
      default: false,
    },
  },

  computed: {
    logoClasses() {
      return {
        'logo-storyblok--text-white': this.isWhiteTextVariant,
      }
    },

    isWhiteTextVariant() {
      return this.textVariant === 'white'
    },

    viewBox() {
      return this.iconOnly ? '0 0 33 33' : '0 0 158 33'
    },

    width() {
      return this.iconOnly ? '33px' : '158px'
    },
  },
}
